import axios from 'axios'
import { 
  API_CONFIG, 
  ERROR_MESSAGES, 
  SORT_DIRECTIONS, 
  DEFAULT_VALUES 
} from '@/constants/content'

const state = {
  products: [],
  loading: false,
  error: null,
  totalPages: 0,
  totalItems: 0,
  currentPage: DEFAULT_VALUES.DEFAULT_PAGE,
  itemsPerPage: DEFAULT_VALUES.ITEMS_PER_PAGE
}

const getters = {
  sortedProducts: (state) => {
    return [...state.products].sort((a, b) => {
      return a.price - b.price
    })
  },
  totalItems: (state) => state.totalItems
}

const mutations = {
  SET_PRODUCTS(state, products) {
    state.products = products
  },
  SET_LOADING(state, loading) {
    state.loading = loading
  },
  SET_ERROR(state, error) {
    state.error = error
  },
  SET_TOTAL_PAGES(state, totalPages) {
    state.totalPages = totalPages
  },
  SET_CURRENT_PAGE(state, page) {
    state.currentPage = page
  }
}

const actions = {
  async fetchProducts({ commit, state }, { page = DEFAULT_VALUES.DEFAULT_PAGE, sort = SORT_DIRECTIONS.ASC } = {}) {
    commit('SET_LOADING', true)
    commit('SET_ERROR', null)

    try {
      // Calculate pagination parameters
      const limit = state.itemsPerPage
      const skip = (page - 1) * limit

      // Fetch products from API
      const response = await axios.get(`${API_CONFIG.BASE_URL}?limit=${limit}&skip=${skip}`)

      let products = response.data.products || []

      // Apply sorting
      if (sort === SORT_DIRECTIONS.ASC) {
        products.sort((a, b) => a.price - b.price)
      } else if (sort === SORT_DIRECTIONS.DESC) {
        products.sort((a, b) => b.price - a.price)
      }

      // Calculate total pages
      const totalProducts = response.data.total || products.length
      const totalPages = Math.ceil(totalProducts / limit)

      commit('SET_PRODUCTS', products)
      commit('SET_TOTAL_PAGES', totalPages)
      commit('SET_CURRENT_PAGE', page)

    } catch (error) {
      commit('SET_ERROR', ERROR_MESSAGES.FETCH_PRODUCTS_ERROR)
      commit('SET_PRODUCTS', [])
    } finally {
      commit('SET_LOADING', false)
    }
  }
}

export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
}
