import { ImageCell, PriceCell, DescriptionCell, DiscountCell, AvailabilityCell, RatingCell, DefaultCell } from './cells';
export default {
  name: 'TableCell',
  components: {
    ImageCell,
    PriceCell,
    DescriptionCell,
    DiscountCell,
    AvailabilityCell,
    RatingCell,
    DefaultCell
  },
  props: {
    type: {
      type: String,
      default: 'default'
    },
    value: {
      type: [String, Number, Object],
      default: ''
    },
    alt: {
      type: String,
      default: ''
    },
    comment: {
      type: String,
      default: ''
    }
  },
  computed: {
    cellComponent() {
      const components = {
        image: 'ImageCell',
        price: 'PriceCell',
        description: 'DescriptionCell',
        discount: 'DiscountCell',
        availability: 'AvailabilityCell',
        rating: 'RatingCell'
      };
      return components[this.type] || 'DefaultCell';
    },
    cellProps() {
      const baseProps = {
        value: this.value
      };
      if (this.type === 'image') {
        return {
          ...baseProps,
          alt: this.alt
        };
      }
      if (this.type === 'rating') {
        return {
          ...baseProps,
          comment: this.comment
        };
      }
      return baseProps;
    }
  }
};
