  PRICE: 'price',
  CATEGORY: 'category',
  BRAND: 'brand',
  STOCK: 'stock',
  DISCOUNT_PERCENTAGE: 'discountPercentage',
  RATING: 'rating'
}

// Table Column Widths
export const TABLE_COLUMN_WIDTHS = {
  IMAGE: 120,
  PRODUCT_NAME: 200,
  PRICE: 100,
  CATEGORY: 150,
  BRAND: 120,
  STOCK: 80,
