import { 
  DEFAULT_VALUES, 
  AVAILABILITY_STATUS, 
  AVAILABILITY_CLASSES, 
  RATING_MESSAGES, 
  CSS_VALUES 
} from '@/constants/content'

export const getColStyle = (field) => {
  const width = field.width
  if (width) return `width: ${width}px; min-width: ${width}px;`
  return undefined
}

export const handleImageError = (event) => {
  event.target.style.display = CSS_VALUES.DISPLAY_NONE
}

export const truncateText = (text, maxLength) => {
  if (!text) return ''
  return text.length > maxLength ? text.substring(0, maxLength) + DEFAULT_VALUES.TRUNCATE_SUFFIX : text
}

export const formatPrice = (price) => {
  if (!price && price !== 0) return ''
  return `${DEFAULT_VALUES.CURRENCY_SYMBOL}${price.toFixed(DEFAULT_VALUES.PRICE_DECIMAL_PLACES)}`
}

export const formatDiscount = (discount) => {
  if (!discount && discount !== 0) return ''
  return `${DEFAULT_VALUES.PERCENTAGE_SYMBOL}${discount}`
}

export const formatAvailability = (status) => {
  if (!status) return AVAILABILITY_STATUS.UNKNOWN
  return status === AVAILABILITY_STATUS.IN_STOCK ? AVAILABILITY_STATUS.IN_STOCK : AVAILABILITY_STATUS.OUT_OF_STOCK
}

export const getAvailabilityClass = (status) => {
  if (!status) return AVAILABILITY_CLASSES.UNKNOWN
  return status === AVAILABILITY_STATUS.IN_STOCK ? AVAILABILITY_CLASSES.IN_STOCK : AVAILABILITY_CLASSES.OUT_OF_STOCK
}

export const formatDimensions = (dimensions) => {
  if (!dimensions) return ''
  const { width, height, depth } = dimensions
  if (!width || !height || !depth) return ''
  return `${width}${DEFAULT_VALUES.DIMENSION_SEPARATOR}${height}${DEFAULT_VALUES.DIMENSION_SEPARATOR}${depth}`
}

export const formatWeight = (weight) => {
  if (!weight && weight !== 0) return ''
  return `${weight}${DEFAULT_VALUES.WEIGHT_UNIT}`
}

export const getNestedValue = (obj, path) => {
  if (!obj || !path) return ''
  return path.split('.').reduce((current, key) => {
    return current && current[key] !== undefined ? current[key] : ''
  }, obj)
}

export const getRatingComment = (item) => {
  if (!item || !item.reviews || !item.reviews.length) return RATING_MESSAGES.NO_REVIEWS

  const firstReview = item.reviews[0]
  if (firstReview && firstReview.comment) {
    return firstReview.comment
  }

  return RATING_MESSAGES.NO_COMMENT
}

export const capitalizeFirstLetter = (string) => {
  if (!string) return ''
  return string.charAt(0).toUpperCase() + string.slice(1)
}
